mapper.select:
SELECT


,jituanwnehua.jituanwnehua_uuid_number as jituanwnehuaUuidNumber
,jituanwnehua.jituanwnehua_name as jituanwnehuaName
,jituanwnehua.jituanwnehua_photo as jituanwnehuaPhoto
,jituanwnehua.jituanwnehua_video as jituanwnehuaVideo
,jituanwnehua.jituanwnehua_types as jituanwnehuaTypes
,jituanwnehua.jituanwnehua_clicknum as jituanwnehuaClicknum
,jituanwnehua.zan_number as zanNumber
,jituanwnehua.cai_number as caiNumber
,jituanwnehua.jituanwnehua_content as jituanwnehuaContent
,jituanwnehua.jituanwnehua_delete as jituanwnehuaDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.yonghu_delete as yonghuDelete

FROM jituanwnehua_liuyan  a
left JOIN jituanwnehua jituanwnehua ON a.jituanwnehua_id = jituanwnehua.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.jituanwnehua_id = #{params.jituanwnehuaId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.jituanwnehua_liuyan_text like CONCAT('%',#{params.jituanwnehuaLiuyanText},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


        and a.reply_text like CONCAT('%',#{params.replyText},'%')


          and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


          and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 


        and a.update_time = #{params.updateTime}




        and a.jituanwnehua_id IS NOT NULL


        and jituanwnehua.jituanwnehua_uuid_number like CONCAT('%',#{params.jituanwnehuaUuidNumber},'%')


        and jituanwnehua.jituanwnehua_name like CONCAT('%',#{params.jituanwnehuaName},'%')


        and jituanwnehua.jituanwnehua_video like CONCAT('%',#{params.jituanwnehuaVideo},'%')


        and jituanwnehua.jituanwnehua_types = #{params.jituanwnehuaTypes}



          and jituanwnehua.jituanwnehua_clicknum >= #{params.jituanwnehuaClicknumStart}   


          and jituanwnehua.jituanwnehua_clicknum <= #{params.jituanwnehuaClicknumEnd}   


        and jituanwnehua.jituanwnehua_clicknum = #{params.jituanwnehuaClicknum}


          and jituanwnehua.zan_number >= #{params.zanNumberStart}   


          and jituanwnehua.zan_number <= #{params.zanNumberEnd}   


        and jituanwnehua.zan_number = #{params.zanNumber}


          and jituanwnehua.cai_number >= #{params.caiNumberStart}   


          and jituanwnehua.cai_number <= #{params.caiNumberEnd}   


        and jituanwnehua.cai_number = #{params.caiNumber}


        and jituanwnehua.jituanwnehua_content like CONCAT('%',#{params.jituanwnehuaContent},'%')


          and jituanwnehua.jituanwnehua_delete >= #{params.jituanwnehuaDeleteStart}   


          and jituanwnehua.jituanwnehua_delete <= #{params.jituanwnehuaDeleteEnd}   


        and jituanwnehua.jituanwnehua_delete = #{params.jituanwnehuaDelete}


          and UNIX_TIMESTAMP(jituanwnehua.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(jituanwnehua.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


          and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


        and yonghu.yonghu_delete = #{params.yonghuDelete}


          and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


order by a.${params.sort} ${params.order}
