mapper.select:
SELECT


,xinwen.xinwen_name as xinwenName
,xinwen.xinwen_types as xinwenTypes
,xinwen.xinwen_photo as xinwenPhoto
,xinwen.zan_number as zanNumber
,xinwen.cai_number as caiNumber
,xinwen.xinwen_clicknum as xinwenClicknum
,xinwen.xinwen_content as xinwenContent
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.yonghu_delete as yonghuDelete

FROM xinwen_collection  a
left JOIN xinwen xinwen ON a.xinwen_id = xinwen.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.xinwen_id = #{params.xinwenId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.xinwen_collection_types = #{params.xinwenCollectionTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}




        and a.xinwen_id IS NOT NULL


        and xinwen.xinwen_name like CONCAT('%',#{params.xinwenName},'%')


        and xinwen.xinwen_types = #{params.xinwenTypes}



          and xinwen.zan_number >= #{params.zanNumberStart}   


          and xinwen.zan_number <= #{params.zanNumberEnd}   


        and xinwen.zan_number = #{params.zanNumber}


          and xinwen.cai_number >= #{params.caiNumberStart}   


          and xinwen.cai_number <= #{params.caiNumberEnd}   


        and xinwen.cai_number = #{params.caiNumber}


          and xinwen.xinwen_clicknum >= #{params.xinwenClicknumStart}   


          and xinwen.xinwen_clicknum <= #{params.xinwenClicknumEnd}   


        and xinwen.xinwen_clicknum = #{params.xinwenClicknum}


          and UNIX_TIMESTAMP(xinwen.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(xinwen.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


        and xinwen.xinwen_content like CONCAT('%',#{params.xinwenContent},'%')



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


          and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


        and yonghu.yonghu_delete = #{params.yonghuDelete}


          and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


order by a.${params.sort} ${params.order}
